function convertDate(db_entry) {
    var year = db_entry.slice(0, 4);
    var month = db_entry.slice(4, 6);
    if (month === "01") {
        month = "Gennaio"
    }
    else if (month === "02") {
        month = "Febbraio"
    }
    else if (month === "03") {
        month = "Marzo"
    }
    else if (month === "04") {
        month = "Aprile"
    }
    else if (month === "05") {
        month = "Maggio"
    }
    else if (month === "06") {
        month = "Giugno"
    }
    else if (month === "07") {
        month = "Luglio"
    }
    else if (month === "08") {
        month = "Agosto"
    }
    else if (month === "09") {
        month = "Settembre"
    }
    else if (month === "10") {
        month = "Ottobre"
    }
    else if (month ==="11") {
        month = "Novembre"
    }
    else if (month === "12") {
        month = "Dicembre"
    }
    var day = db_entry.slice(6, 8).replace(/\b0+/, '');
    return day + " " + month + " " + year
}

function convertHour(db_entry) {
    return db_entry.slice(0, 2).replace(/\b0+/, '') + ":" + db_entry.slice(2, 4)
}

function getCookie(cookiename) 
  {
  // Get name followed by anything except a semicolon
  var cookiestring=RegExp(cookiename+"=[^;]+").exec(document.cookie);
  // Return everything after the equal sign, or an empty string if the cookie name is not found
  return decodeURIComponent(!!cookiestring ? cookiestring.toString().replace(/^[^=]+./,"") : "");
  }

